import React from "react";
import "./ToDolIstItem.css";

class ToDoListItem extends React.Component {
  constructor() {
    super();
    // this.state = {
    //   done: false,
    //   tant: false
    // };
    // this.onLabelClick = () => {
    //   this.setState(({ done }) => {
    //     return {
    //       done: !done
    //     };
    //   });
    // };
    this.onMakeImportant = () => {
      this.setState(({tant}) => {
        return {
          tant: !tant
        };
      });
    };
  }

  render() {
    const { label,onDeleted,onToggleDone } = this.props;
    const { done, tant } = this.state;
    let classNames = "list-group-item";
    if (done) {
      classNames += " done";
    }
    if (tant) {
      classNames += " important";
    }

    return (
      <span className={classNames}>
        <span className="list-group-item-label" onClick={this.onToggleDone}>
          {label}
        </span>
        <button
          type="button"
          className="btn btn-outline-success btn-sm float-right"
          onClick={this.onMakeImportant}
        >
          <i className="fa fa-exclamation" />
        </button>
        <button
          type="button"
          className="btn btn-outline-danger btn-sm float-right" onClick={onDeleted}
        >
          <i className="fa fa-trash-o" />
        </button>
      </span>
    );
  }
}
