import React from "react";
import "./AppItemAddForm.css";

export default class AppItemAddForm extends React.Component {

    render() {
        let {value} = this.props;         
    return (
      <form className="d-flex item-add-form">
        <input type="text"   onChange={(evt)=>this.props.newItemAdd(evt)}/>
        <button
          type="submit"
          className="btn btn-outline-secondary"
          onSubmit={() => this.props.onItemAdded(value)}>add item
        </button>
      </form>
    );
  }
}
