import React from "react";
import AppToDoList from "../AppToDoList";
import AppSearchPanel from "../AppSearchPanel";
import AppHeader from "../AppHeader";
import "./App.css";
import AppItemAddForm from "../AppItemAddForm";

export default class App extends React.Component {
  maxId = 100;
  state = {
    toDoData: [
      this.createToDoItem("drink coffee"),
      this.createToDoItem("eat icecream"),
      this.createToDoItem("buy car")
    ],
    inputValue: ""
  };

  createToDoItem(label) {
    return {
      label,
      tant: false,
      done: false,
      id: this.maxId++
    };
  }

  deleteItem = id => {
    this.setState(({ toDoData }) => {
      let filteredToDoData = toDoData.filter(item => item.id !== id);
      console.log(filteredToDoData);
      return {
        toDoData: filteredToDoData
      };
    });
  };
  addItem = text => {
    let newItem = this.createToDoItem(text);
    this.setState(({ toDoData }) => {
      let newArr = [...toDoData, newItem];
      return {
        toDoData: newArr
      };
    });
  };
  updateValue = evt => {
    this.setState({ inputValue: evt.target.value });
  };
  onToggleDone = id => {
    this.setState(({ toDoData }) => {
      console.log(toDoData);
      const idx = toDoData.findIndex(el => el.id === id);
      const oldItem = toDoData[idx];
  const newItem = {...oldItem,done:!oldItem.done};
  console.log(newItem);
 const newArr = [...toDoData.slice(0,idx),newItem,...toDoData.slice(idx+1)];
  return {
toDoData:newArr
  }
    });
  };
onToggleImportant=(id)=>{
  this.setState(({toDoData})=>{
    const idx = toDoData.findIndex((el)=>el.id = id);
    const newItem = toDoData[idx];
  const newArr = [...toDoData.slice(0,idx),newItem,...toDoData.slice(idx+1)];
return{
  toDoData:newArr
}
  })
}
  render() {
    let doneCount = this.state.toDoData.filter(el => el.done).length;
    let toDoCount = this.state.toDoData.length - doneCount;
    return (
      <div className="header d-flex">
        <div>
          <AppHeader done={doneCount} todo={toDoCount} />
          <AppSearchPanel />
          <AppToDoList
            todos={this.state.toDoData}
            onDeleted={this.deleteItem}
            onToggleDone={this.onToggleDone}
          />
          <AppItemAddForm
            value={this.state.inputValue}
            onItemAdded={this.addItem}
            newItemAdd={this.updateValue}
          />
        </div>
      </div>
    );
  }
}
