import React from 'react';
import './SeacchPanel.css';
import ItemsStatusFilter from "../AppItemsStatusFilter";

export default class SearchPanel extends React.Component {
state={
    term:''
}
onSearchChange = (e) =>{
let term = e.targer.value;
this.setState({term})
}
render() {
return (
    <div className="d-flex search-main">
    <input type="text" placeholder="search" onChange={this.onSearchChange} value={this.state.term}  />
    <ItemsStatusFilter />
    </div>
);
}
}
