import React from "react";
import AppToDoList from "../AppToDoList";
import AppSearchPanel from "../AppSearchPanel";
import AppHeader from "../AppHeader";
import "./App.css";
import AppItemAddForm from "../AppItemAddForm";

export default class App extends React.Component {
  maxId = 100;
  state = {
    toDoData: [
      {label:'drink coffee,tant:false,done:true,id:1'}
      // this.createToDoItem("drink coffee"),
      // this.createToDoItem("eat icecream"),
      // this.createToDoItem("buy car")
    ],
    inputValue: ""
  }
  
  createToDoItem(label) {
    return {
      label,
      tant: false,
      done: false,
      id: this.maxId++
    }
  }
  
  deleteItem = id => {
    this.setState(({ toDoData }) => {
      let filteredToDoData = toDoData.filter(item => item.id !== id);
      console.log(filteredToDoData);
      return {
        toDoData: filteredToDoData
      };
    });
  };
  addItem = text => {
    let newItem = this.createToDoItem(text);
    this.setState(({ toDoData }) => {
      let newArr = [...toDoData, newItem];
      return {
        toDoData: newArr
      };
    });
  };
  updateValue = evt => {
    this.setState({ inputValue: evt.target.value });
  };
  onToggleDone=(id)=> {
    console.log('from ontoggle');
  }
  
  render() {
    console.log(this.createToDoItem('created new item'));
    console.log('from App',this.state);
    
    let {toDoData} =this.state;
    let toDoCount = this.state.toDoData.filter((el)=>el.done).length;
    let toDoDone = this.state.toDoData.length =toDoCount;
    return (
      <div className="header d-flex">
        <div>
          <AppHeader done={1} todo={1} />
          <AppSearchPanel />
          <AppToDoList
            todos={toDoData}
            onDeleted={this.deleteItem}
          />
          <AppItemAddForm
            value={this.state.inputValue}
            onItemAdded={this.addItem}
            newItemAdd={this.updateValue}
          />
        </div>
      </div>
    );
  }
}
