import React from "react";
import "./ItemList.css";
import SwapiService from "../../services/SwapiService";
import Spinner from "../Spinner";

export default class ItemList extends React.Component {
  state = {
    itemList: null
  };

  componentDidMount() {
    this.updataPeopleList();
  }

  onPeopleLoaded = itemList => {
    this.setState({ people });
  };
  updataPeopleList() {
    this.props.getData().then(this.onPeopleLoaded);
  }

  randerItems(arr) {
    return arr.map(({ id, name }) => {
      return (
        <li
          className="list-group-item"
          key={id}
          onClick={() => this.props.onSelectPerson(id)}
        >
          {name}
        </li>
      );
    });
  }

  render() {
    if (!this.state.people) {
      return <Spinner />;
    }
    const items = this.randerItems(this.state.people);

    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">{items}</ul>
      </div>
    );
  }
}
