import React from "react";
import "./ItemList.css";
import Spinner from "../Spinner";

// export default
class ItemList extends React.Component {

 
  

  randerItems(arr) {
    return arr.map(item => {
      const { id } = item;
      const label = this.props.children(item);
      return (
        <li
          className="list-group-item"
          key={id}
          onClick={() => this.props.onSelectPerson(id)}
        >
          {label}
        </li>
      );
    });
  }

  render() {

    if (!this.state.data) {
      return <Spinner />;
    }
    const items = this.randerItems(this.state.data);

    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">{items}</ul>
      </div>
    );
  }
}

const withData = (view) => {
  return class extends React.Component {
    state = {
      data: null
    };

    componentDidMount() {
      this.updataPeopleList();
    }

    updataPeopleList() {
    this.props.getData().then(this.onDataLoaded);
  }
  
  onDataLoaded = data => {
    this.setState({ data });
  };
    render() {
      if (!this.state.data) {
        return <Spinner />
      }
      return <ItemList {...this.props} data={this.state.data} />;
    }
  };
};
export default withData(I);
