import React from "react";
import "./RandomPlanet.css";
import SwapiService from "../../services/SwapiService";
import Spinner from '../Spinner';

export default class RandomPlanet extends React.Component {
  SwapiService = new SwapiService();
  state = {
    planet: {},
    loading:true
  };
  componentDidMount() {
    this.updatePlanet();
  }

  onPlanetLoaded = (planet) => {
    this.setState({ planet,loading:false });
  };

  updatePlanet=()=> {
    const id = Math.floor(Math.random() * 25) + 2;

    this.SwapiService
    .getPlanet(id)
    .then(this.onPlanetLoaded);
  }

  render() {
    console.log(this.state);    
const spinner = this.state.loading?<Spinner />:null
    return (
        {spinner}
    
    );
  }
}

class PageView extends React.Component{
    render(){
        return(
            <div className="d-flex random-planet">
            <img src={`https://starwars-visualguide.com/assets/img/planets/${this.state.planet.id}.jpg`} />
            <div className="">
              <ul className="list-group">
                <li>
                  <h3>Planet</h3>
                </li>
    
                <li className="list-group-item">
                  <span>name</span> <span>{this.state.planet.name}</span>
                </li>
                <li className="list-group-item">
                  <span>diameter</span> <span>{this.state.planet.diameter}</span>
                </li>
                <li className="list-group-item">
                  <span>population</span> <span>{this.state.planet.population}</span>
                </li>
                <li className="list-group-item">
                  <span>climate</span> <span>{this.state.planet.climate}</span>
                </li>
                <li className="list-group-item">
                  <span>terrain</span> <span>{this.state.planet.terrain}</span>
                </li>
                <li className="list-group-item">
                  <span>rotation period</span> <span>{this.state.planet.rotationPeriod}</span>
                </li>
              </ul>
            </div>
          </div>
        )
    }
}
