import React from 'react';
import ReactDom from 'react-dom';
import App from './components/App';

ReactDom.render(<App />,document.querySelector('#root'));


class SwapoService{
    _apiBase= 'https://swapi.co/api'
async getResourse(url){
const res = await fetch(`${this._apiBase}${url}`);
if (!res.ok) {
    throw new Error(`could not fetch ${url} received ${res.status}`)
}
return await res.json();
}
async getAllPeople() {
    const res = await fetch(this.getResourse('/people/'));
    return res;
}
async getPerson(id) {
    const res = await fetch(this.getResourse(`/people/${id}`));
    return res.results;
}
async getAllPlanets() {
    const res = await fetch(this.getResourse('/planets/'));
    return res.results;
}
async getPlanet(id) {
    const res = await fetch(this.getResourse(`/planets/${id}/`));
    console.log(res);
}
}

let swapi = new SwapoService();

swapi.getAllPeople().then((res)=>console.log(res));
