import React from 'react';
import {StarshipList,StarshipDetails} from '../SwComponents';
import Row from '../Row';

export default class StarshipPage extends React.Component{
    state={
        selectedItem:null
    }
onItemSelected=(selectedItem)=>{    
this.setState({selectedItem})
}

    render(){
        return(
            <Row left={<StarshipList onItemSelected={this.onItemSelected}/>} right={<StarshipDetails itemId={this.state.selectedItem} />}/>
        )
    }
}
