import React from "react";
import "./ItemList.css";
import SwapiService from "../../services/SwapiService";

export default class ItemList extends React.Component {
  state = {
    people: null
  };
  swapiService = new SwapiService();

  componentDidMount() {
    this.updataPeopleList();
  }

  onPeopleLoaded = people => {
    this.setState({ people });
  };
  updataPeopleList() {
    this.swapiService.getAllPeople().then(this.onPeopleLoaded);
  }

  randerItems(arr) {
    console.log(arr);

    return arr.map((item) => {
      return (
        <li className="list-group-item" key={item.id}>
          {item.name}
        </li>
      );
    });
  }
  render() {
    const x = arr => {
      arr.map(({ id, name }) => {
        return (
          <li>
            key={id} my name={name}
          </li>
        );
      });
    };
    console.log(x(this.state.people));
    // const items = this.randomItems(people);
    console.log(this.state);
    console.log(x);

    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">{/* {items} */}</ul>
      </div>
    );
  }
}
