import React from 'react';
import {PlanetsList,PlanetsDetails} from '../SwComponents';
import Row from '../Row';

export default class PlanetsPage extends React.Component{
    state={
        selectedItem:null
    }
onItemSelected(selectedItem) {
    this.setState({selectedItem})
}

    render(){
        return(
            <Row left={<PlanetsDetails itemId={this.state.selectedItem}/>} right={<PlanetsList onItemSelected={this.onItemSelected}/>} />
        )
    }
}
