import React from "react";
import Header from "../Header";
import ItemList from "../ItemList";
import RendomPlanet from "../RandomPlanet";
import PersonDetails from "../PersonDetails";
import ErrorButton from '../ErrorButton';
import './App.css';

export default class App extends React.Component {
  state={
    showRandomPlanet:true,
    selectedPerson:null,
    hasError:false
  }
  onPersonSelected =(id)=>{
this.setState({selectedPerson:id});
  }
  toggleRandomPlanet=()=>{
    this.setState({showRandomPlanet:!this.state.showRandomPlanet});
  }
  componentDidCatch() {
    this.setState({error:true});
  }
  render() {
    if (this.state.hasError) {
      
    }
    const planet = this.state.showRandomPlanet?<RendomPlanet />:null;
    return (
      <div className="star-db">
        <Header />
        {planet}
        <input type="button" className="btn btn-warning" value="toggle random planet" onClick={this.toggleRandomPlanet}/>
        <ErrorButton />
        <div className="d-flex ">
          <ItemList onSelectPerson={this.onPersonSelected}/>
          <PersonDetails personId={this.state.selectedPerson}/>
        </div>
      </div>
    );
  }
}
