import React from "react";
import './RandomPlanet.css';
import SwapiService from '../../services/SwapiService';

export default class PlanetDetails extends React.Component {
  swapiService = new SwapiService(); 
    state= {
        name:'earth',
        population:null,
        rotation:null,
        diameter:null
    }
    updatePlanet() {
      this.swapiService.getPlanet(7).then((planet)=>{
        this.setState({
          name:planet.name,
          population:planet.population,
          rotation:planet.rotation,
          diameter:planet.diameter
        })
      })
    }

  render() {
console.log(this.state);
    
    return (
      <div className="d-flex random-planet">
        <img className="planet-image" alt="planet" src="https://starwars-visualguide.com/assets/img/planets/4.jpg/" />
        <div>
          <h4>{this.state.name}</h4>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <span className="term">Population</span>
              <span>{this.state.population}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Rotation period</span>
              <span>{this.state.rotation}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Diameter</span>
              <span>{this.state.diameter}</span>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}
