import React from 'react';
import ItemList from '../ItemList';
import PersonDetails from '../PersonDetails';
import SwapiService from '../../services/SwapiService';
import ErrorIndicator from '../ErrorIndicator';

export default class PeopleList extends React.Component {
    state={
        selectedPlanet:null,
        hasError:false
      }

     swapiService= new SwapiService();
  componentDidCatch() {
      this.setState({hasError:true});
  }

  onPersonSelected =(id)=>{
this.setState({selectedPlanet:id});
  }
componentDidCatch(error,info) {
    debugger;
    this.setState({hasError:true});
}
 render() {
     if (this.state.hasError) {
         return <ErrorIndicator />
     }
     return (
        <div className="d-flex planet-page">
        <ItemList onSelectPerson={this.onPersonSelected} getData={this.swapiService.getAllPlanets} renderItem={(item) =>{
           console.log('item',item);           
         return   item.name}
          }            />
        <PersonDetails personId={this.state.selectedPlanet}/>
      </div>
     )
 }   
}
