import React from "react";
import "./RandomPlanet.css";
import SwapiService from "../../services/SwapiService";
import Spinner from '../Spinner';
import ErrorIndicator from '../ErrorIndicator';

export default class PlanetDetails extends React.Component {
  swapiService = new SwapiService();
  state = {
  planet:{},
  loading:true,
  error:false
  };
  constructor() {
    super();
    this.updatePlanet();
  }
  onPlanetLoaded=(planet)=> {
    this.setState({ planet,loading:false })
  }
  onError= (err)=>{
this.setState({error:true,loading:false});
  }
  updatePlanet=() =>{
    // const id = Math.floor(Math.random() * 25) + 2;   
    const id= 12222; 
  this.swapiService.getPlanet(id).then(this.onPlanetLoaded).catch(this.onError);
  }

  render() {    
    const hasMessage = !(this.state.loading ||this.state.error);
    const spinner = hasMessage? <Spinner />:<PlanetView planet={this.state.planet}/>;
  const errorMsg = this.state.error?<ErrorIndicator />:null;
    return (
      <div className="d-flex random-planet card">
        {errorMsg}
     {spinner}
      
    
      </div>
    ); 
  }
}

const PlanetView = ({planet}) =>{
  console.log(planet);
  
  return(
    <React.Fragment>
           <img
          className="planet-image"
          alt="planet"
          src={`https://starwars-visualguide.com/assets/img/planets/${planet.id}.jpg`}
        />
        <div className="card-body">
          <h4>{planet.name}</h4>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <span className="term">Population: </span>
              <span>{planet.population}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Rotation period: </span>
              <span>{planet.rotation}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Diameter: </span>
              <span>{planet.diameter}</span>
            </li>
          </ul>
        </div>
    </React.Fragment>
  )
}
