import React from "react";
import Header from "../Header";
import RendomPlanet from "../RandomPlanet";
import ErrorButton from "../ErrorButton";
import "./App.css";
import ErrorIndicator from "../ErrorIndicator";
import Row from "../Row";
import ItemDetails from "../ItemDetails";
import SwapiService from "../../services/SwapiService";
import { Record } from "../ItemDetails/ItemDetails";

export default class App extends React.Component {
  state = {
    showRandomPlanet: true,
    hasError: false
  };
  swapiService = new SwapiService();

  toggleRandomPlanet = () => {
    this.setState({ showRandomPlanet: !this.state.showRandomPlanet });
  };
  componentDidCatch() {
    this.setState({ hasError: true });
  }
  render() {
    if (this.state.hasError) {
      return <ErrorIndicator />;
    }
    const planet = this.state.showRandomPlanet ? <RendomPlanet /> : null;

    const {
      getPerson,
      getPlanet,
      getStarship,
      getPersonImage,
      getStarshipImage,
      getPlanetImage
    } = this.swapiService;

    const personDetails = (
      <ItemDetails itemId={1} getData={getPerson} getImageUrl={getPersonImage}>
        <Record field="gender" label="Gender" />
        <Record field="eyeColor" label="Eye color" />
        <Record field="birthYear" label="Birth year" />
      </ItemDetails>
    );

    const planetDetails = (
      <ItemDetails
        itemId={2}
        getData={getPlanet}
        getImageUrl={getPlanetImage}
      >
        <Record field="name" label="name"/>
        <Record field="diameter" label="diameter"/>
        <Record field="name" label="name"/>
      </ItemDetails>
    );

    return (
      <div className="star-db">
        <Header />
        {planet}
        <div>
          <input
            type="button"
            className="btn btn-warning"
            value="toggle random planet"
            onClick={this.toggleRandomPlanet}
          />
          <ErrorButton />
        </div>
        <div>
          <Row left={personDetails} right={planetDetails} />
        </div>
      </div>
    );
  }
}
