import React from 'react';
import './PersonDetails.css';
import SwapiService from '../../services/SwapiService';

export default class PersonDetails extends React.Component {
    SwapiService = new SwapiService();
    state= {
        person:null
    }
    updatePerson=()=>{
        this.SwapiService.getPerson(this.props.personId).then((person)=>{
            this.setState({person});
        })
    }
render() {
    return(
<div className="d-flex person-details">
<img
src="https://starwars-visualguide.com/assets/img/characters/3.jpg" />
<ul className="list-group list-group-flush">
    <li className="list-group-item"><span>name</span><span>luke</span></li>
    <li className="list-group-item"><span>age</span><span>22</span></li>
    <li className="list-group-item"><span>height</span><span>179</span></li>
    <li className="list-group-item"><span>gender</span><span>male</span></li>
</ul>
</div>
    )
}
}
