import React from "react";
import "./PersonDetails.css";
import SwapiService from "../../services/SwapiService";

export default class PersonDetails extends React.Component {
  SwapiService = new SwapiService();
  state = {
    person: null
  };
  componentDudMount() {
    this.updatePerson();
  }

  componentDidUpdate(prevProps) {
    console.log("updated");
    if (this.props.personId !== prevProps.personId) {
      this.updatePerson();
    }
  }
  updatePerson = () => {
    if (!this.props.personId) {
      return;
    }
    this.SwapiService.getPerson(this.props.personId).then(person => {
      this.setState({ person });
    });
  };
  render() {
    console.log("props", this.props);

    console.log("state", this.state);

    if (!this.state.person) {
      return (
     
          <div className="details-placeholder">Select person from the list</div>
      
      );
    }
    const { id, name, eyeColor, gender, birthYear, mass } = this.state.person;

    return (
      <div className="d-flex person-details">
        {" "}
        <img
          src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
        />
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            <span>name </span>
            <span>{name}</span>
          </li>
          <li className="list-group-item">
            <span>eyeColor </span>
            <span>{eyeColor}</span>
          </li>
          <li className="list-group-item">
            <span>gender </span>
            <span>{gender}</span>
          </li>
          <li className="list-group-item">
            <span>birth year </span>
            <span>{birthYear}</span>
          </li>
          <li className="list-group-item">
            <span>mass </span>
            <span>{mass}</span>
          </li>
        </ul>
      </div>
    );
  }
}
