import React from "react";
import "./PersonDetails.css";
import SwapiService from "../../services/SwapiService";

export default class PersonDetails extends React.Component {
state= {
  person:{}
}
swapiService= new SwapiService();

componentDidMount() {
  this.updatePerson();
}

updatePerson=()=>{
const {personId} = this.props;
if (!personId) {
  return;
}
this.swapiService.getPerson(personId).then((person)=> {this.setState({person})});
}

  render() {
    console.log(this.state.person);
    
    const {id,name,gender,eyeColor,birth,height} = this.state.person;
    return (
      <div className="card person-details d-flex">
        <img
          className="person-image"
          src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
        />

        <div className="card-body">
          <h4>{name}{this.state.person.id}</h4>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <span className="term">Gender</span>
              <span>{gender}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Birth Year</span>
              <span>{birth}</span>
            </li>
            <li className="list-group-item">
              <span className="term">eyeColor</span>
              <span>{eyeColor}r</span>
            </li>
            <li className="list-group-item">
              <span className="term">Height</span>
              <span>{height}r</span>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}
