export default class SwapiService {
  _apiBase = "https://swapi.co/api"

  getResourse = async url => {
    const res = await fetch(`${this._apiBase}${url}`);
    return res.json();
  };
    getAllPeople= async()=> {
      let res = await this.getResourse("/people/");            
      return res.results.map(this._transformPeople);
    };
    getPerson = async (id) =>{
        const res = await this.getResourse(`/people/${id}/`);
        return res;
    };
   getAllPlanets= async ()=> {
      let res = await this.getResourse("/planets/");            
      return res.results.map(this._transformPlanet);
    };
    getPlanet = async (id) =>{
        const planet = await this.getResourse(`/planets/${id}/`);
        return planet;
    };
    _extractId(item) {
      
      return item.url.match(/\/\d+\/$/)[0];
    }
    _transformPeople=(person)=> {
      return{
        id: this._extractId(person),
        name:person.name,
        mass:person.mass,
        birthYear:person.birth_year,
        gender:person.gender,
        eyeColor:person.eye_color
      }
    }
    _transformPlanet=(planet)=> {
      return{
        id:this._extractId(planet),
        name:planet.name,
        diameter:planet.diameter,
        climate:planet.climate,
        rotationPeriod:planet.rotation_period,
        terrain:planet.terrain
      }
    }
}
