import React from "react";
import "./ItemList.css";
import SwapiService from "../../services/SwapiService";
import Spinner from '../Spinner';

export default class ItemList extends React.Component {
  state = {
    people: null
  };
  swapiService = new SwapiService();

  componentDidMount() {
    this.updataPeopleList();
  }

  onPeopleLoaded = people => {
    this.setState({ people });
  };
  updataPeopleList() {
    this.swapiService.getAllPeople().then(this.onPeopleLoaded);
  }

  randerItems(arr) {
    return arr.map(({id,name}) => {
      return (
        <li className="list-group-item" key={id} onClick={()=>this.props.onPersonSelected(id)}>
          {name}
        </li>
      )
    });
  }

  render() {
if (!this.state.people) {
  return <Spinner />
}  
const items = this.randerItems(this.state.people);


    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">
          {items}
        </ul>
      </div>
    );
  }
}
