import React from "react";
import "./ItemList.css";
import Spinner from "../Spinner";

export default class ItemList extends React.Component {
  state = {
    itemList: null
  };

  componentDidMount() {
    this.updataPeopleList();
  }

  onPeopleLoaded = itemList => {
    this.setState({ itemList });
  };
  updataPeopleList() {
    this.props.getData().then(this.onPeopleLoaded);
  }

  randerItems(arr) {
    return arr.map((item) => { 
      const {id} = item;
      const label = this.props.children(item);
      return (
        <li
          className="list-group-item"
          key={id}
          onClick={() => this.props.onSelectPerson(id)}
        >
          {label}
        </li>
      );
    });
  }

  render() {
    console.log('itemlist',this.state.itemList);
    
    if (!this.state.itemList) {
      return <Spinner />;
    }
    const items = this.randerItems(this.state.itemList);

    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">{items}</ul>
      </div>
    );
  }
}
