import React from "react";
import Header from "../Header";
import RandomPlanet from "../RandomPlanet";
import ErrorButton from "../ErrorButton";
import "./App.css";
import ErrorIndicator from "../ErrorIndicator";
import SwapiService from "../../services/SwapiService";
import DummySwapiService from "../../services/DummySwapiService";
import AppContext from "../SwapiServiceContext";
import { PeoplePage, PlanetsPage, StarshipPage } from "../Pages";

export default class App extends React.Component {
  swapiService = new DummySwapiService();

  state = {
    showRandomPlanet: true,
    hasError: false,
    swapiService: new DummySwapiService()
  };

  toggleRandomPlanet = () => {
    this.setState({ showRandomPlanet: !this.state.showRandomPlanet });
  };
  componentDidCatch() {
    this.setState({ hasError: true });
  }
  onServiceChange = () => {
    this.setState(({ swapiService }) => {
      const Service =
        swapiService instanceof SwapiService ? DummySwapiService : SwapiService;
      console.log(Service.name);
      return {
        swapiService: new Service()
      };
    });
  };
  render() {
    console.log(this.state.swapiService);

    if (this.state.hasError) {
      return <ErrorIndicator />;
    }
    const planet = this.state.showRandomPlanet ? <RandomPlanet updateInterval={11111}/> : null;

    return (
      <div className="star-db">
        <Header onServiceShange={this.onServiceChange} />
        {planet}
        <AppContext.Provider value={this.state.swapiService}>
          <div className="toggle-and-error">
            <input
              type="button"
              className="btn btn-warning"
              value="toggle random planet"
              onClick={this.toggleRandomPlanet}
            />
            <ErrorButton />
          </div>
          <PeoplePage />
          <PlanetsPage />
          <StarshipPage />
        </AppContext.Provider>
      </div>
    );
  }
}
