import React from "react";
import Header from "../Header";
import RendomPlanet from "../RandomPlanet";
import ErrorButton from "../ErrorButton";
import "./App.css";
import ErrorIndicator from "../ErrorIndicator";
import SwapiService from "../../services/SwapiService";

// import {SwapiServiceProvider} from '../SwapiServiceContext';

const Provider = React.createContext().Provider;
const Consumer = React.createContext().Consumer;
import {
  PersonList,
  PlanetList,
  PersonDetails,
  PlanetDetails,
  StarshipList,
  StarshipDetails
} from "../SwComponents";

export default class App extends React.Component {
  state = {
    showRandomPlanet: true,
    hasError: false
  };
  swapiService = new SwapiService();

  toggleRandomPlanet = () => {
    this.setState({ showRandomPlanet: !this.state.showRandomPlanet });
  };
  componentDidCatch() {
    this.setState({ hasError: true });
  }
  render() {
    if (this.state.hasError) {
      return <ErrorIndicator />;
    }
    const planet = this.state.showRandomPlanet ? <RendomPlanet /> : null;


    return (
      
      // <SwapiServiceProvider value={this.swapiService}>
      <div className="star-db">
        <Header />
        {planet}
        <div>
          <input
            type="button"
            className="btn btn-warning"
            value="toggle random planet"
            onClick={this.toggleRandomPlanet}
          />
          <ErrorButton />
        </div>
        <div className="d-flex">
        <PersonList />
        
        <PersonDetails itemId={1}/>
        
        </div>
        <div className="d-flex">
        <PlanetList />
        <PlanetDetails itemId={2}/>
        </div>
        <div className="d-flex">
        <StarshipList />
        <StarshipDetails itemId={5} />

        </div>
      </div>
      // </SwapiServiceProvider>
    );
  }
}
