import React from "react";
import "./ItemList.css";
import Spinner from "../Spinner";

// export default
const ItemList = (props)=> {
const {data,onItemSElected, children:renderLabel} = props;
    const items = data.map((item) => {
      const { id } = item;
      const label = renderLabel(item);
      return (
        <li
          className="list-group-item"
          key={id}
          onClick={() => onItemSElected(id)}
        >
          {label}
        </li>
      );
    });

    return (
      <div className="item-list">
        <ul className="list-group list-group-flush">{items}</ul>
      </div>
    );
  }


const withData = (view) => {
  return class extends React.Component {
    state = {
      data: null
    };

    componentDidMount() {
      this.updataPeopleList();
    }

    updataPeopleList() {
    this.props.getData().then(this.onDataLoaded);
  }
  
  onDataLoaded = data => {
    this.setState({ data });
  };
    render() {
      if (!this.state.data) {
        return <Spinner />
      }
      return <ItemList {...this.props} data={this.state.data} />;
    }
  };
};
export default withData(It);
