import React from "react";
import Header from "../Header";
import RendomPlanet from "../RandomPlanet";
import ErrorButton from '../ErrorButton';
import './App.css';
import PeoplePage from '../PeoplePage';
import ErrorIndicator from '../ErrorIndicator';

export default class App extends React.Component {
  state={
    showRandomPlanet:true,
    hasError:false
  }
 

  toggleRandomPlanet=()=>{
    this.setState({showRandomPlanet:!this.state.showRandomPlanet});
  }
  componentDidCatch() {
    this.setState({hasError:true});
  }
  render() {
    if (this.state.hasError) {
      return <ErrorIndicator />
    }
    const planet = this.state.showRandomPlanet?<RendomPlanet />:null;
    return (
      <div className="star-db">
        <Header />
        {planet}
        <input type="button" className="btn btn-warning" value="toggle random planet" onClick={this.toggleRandomPlanet}/>
        <ErrorButton />
    <div>
      <PeoplePage />
      <PeoplePage />
      <PeoplePage />
  
    </div>
      </div>
    );
  }
}
