import React from "react";
import Spinner from "../Spinner/";

const withData = (View) => {
  return class extends React.Component {
    state = {
      data: null
    };
    componentDidMount() {
    this.update();
    }
componentDidUpdate(prevProp){
if(this.state.data !==prevProp.data){
  this.update();
}
}

    update(){
      console.log('withData props',this.props);
      this.props.getData()
      .then(data => {
        this.setState({ data });
      });

    }
    render() {      
      if (!this.state.data) {
        return <Spinner />;
      }
      return <View {...this.props} data={this.state.data} />;
    }
  };
};

export {withData};
