class SwapiService {

_swapiBase = 'https://swapi.co/api/';
    getReasourse = async (url) =>{
        const res = await fetch(url);
        if (!res.ok) {
            throw new Error(`could not fetch ${url} received ${res.status}`);
        }
        const body = res.json();
        return body;
    }
}
getReasourse('https://swapi.co/api/people/11111/').then((res)=>console.log(res));
