import React from 'react';
import './PersonDetails.css';
import SwapiService from '../../services/SwapiService';

export default class PersonDetails extends React.Component {
    SwapiService = new SwapiService();
    state= {
        person:null
    }
    componentDidMount() {
        this.updatePerson();
    }
    updatePerson=()=>{
        this.SwapiService.getPerson(this.props.personId).then((person)=>{
            this.setState({person});
        })
    }
render() {
    console.log('props',this.props);
    
    console.log('state',this.state);

    const details = ({id,name,age,gender,height}) =>{
        return(
            <React.Fragment>

           <img
src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`} />
<ul className="list-group list-group-flush">
    <li className="list-group-item"><span>name</span><span>{name}</span></li>
    <li className="list-group-item"><span>age</span><span>{age}</span></li>
    <li className="list-group-item"><span>gender</span><span>{gender}</span></li>
    <li className="list-group-item"><span>height</span><span>{height}</span></li>
</ul> 
</React.Fragment>
        )
    }
    return(
<div className="d-flex person-details">
{details}
</div>
    )
}
}
