import React from 'react';
import './RandomPlanet.css';
import SwapiService from '../../services/SwapiService';

export default class RandomPlanet extends React.Component {
    SwapiService = new SwapiService()
    state={
        planet:{}
    }
    componentDidMount() {
        this.updatePlanet();
    }
    onPlanetLoaded=(planet)=>{
this.setState({planet});
    }
    
    updatePlanet() {
        const id = Math.floor(Math.random()*25+2);
        console.log(id);
        
        this.SwapiService.getPlanet(id).then(this.onPlanetLoaded);
        
    }
    
    render() {
        console.log(this.state);
        
        
        return(
            <div className="d-flex random-planet">
                <img src={`https://starwars-visualguide.com/assets/img/planets/1.jpg`} />
<div className="">
    <ul className="list-group">
    <li><h3>Planet</h3></li>

    <li className="list-group-item"><span>name</span> <span>Earth</span></li>
    <li className="list-group-item"><span>diameter</span> <span>1111</span></li>
    <li className="list-group-item"><span>population</span> <span>222222</span></li>
    <li className="list-group-item"><span>climate</span> <span>good</span></li>
    <li className="list-group-item"><span>rotation period</span> <span>88</span></li>
    </ul>
</div>
            </div>
        )
    }
}
