class SwapiService {

_swapiBase = 'https://swapi.co/api';

  async  getReasourse(url){
        const res = await fetch(`${this._swapiBase}${url}`);
        if (!res.ok) {
            throw new Error(`could not fetch ${url} received ${res.status}`);
        }
        const body = res.json();
        return body;
    }
    async getAllPeople() {
        const res = await this.getReasourse('/people/');
    return res.results;
    }
    getPerson(id) {
        return this.getReasourse(`/people/${id}`);
    }

}
