import React from "react";
import Spinner from "../Spinner/";

const withData = (View, getData) => {
  return class extends React.Component {
    state = {
      data: null
    };
    componenDidMount() {
      getData().then(data => {
        this.setState({ data });
      });
    }
    render() {
        if (!this.state.data) {
            return <Spinner />
        }
        return(
            <View {...this.props} data={this.state.data}/>
        )
    }
  };
};

export default withData;
