import React from "react";
import "./ItemDetails.css";
import ErrorButton from "../ErrorButton";

export default class ItemDetails extends React.Component {

  state = {
    item: null
  };

  componentDidMount() {
    this.updateItem();
  }

  componentDidUpdate(prevProps) {
    console.log("updated");
    if (this.props.itemId !== prevProps.itemId) {
      this.updateItem();
    }
  }

  update() {
    const {itemId,getData} = this.props;
    console.log(itemId);
    
    if (!itemId) {
      return;
    }
    getData(itemId)
    .then((item) => {
      console.log("item from getData", item);

      this.setState({ item });
    });
  }
  render() {
    console.log("props itemDetails", this.props);

    console.log("state", this.state);

    const { item } = this.state;
    if (!item) {
      return (
        <div className="details-placeholder">Select item from the list</div>
      );
    }
    const { id, name, gender, birthYear, eyeColor, mass } = this.state.item;

    return (
      <div className="d-flex person-details">
        {" "}
        <img
          src={`https://starwars-visualguide.com/assets/img/characters/${id}.jpg`}
        />
        <ul className="list-group list-group-flush">
          <ErrorButton />
          <li className="list-group-item">
            <span>name </span>
            <span>{name}</span>
          </li>
          <li className="list-group-item">
            <span>eyeColor </span>
            <span>{eyeColor}</span>
          </li>
          <li className="list-group-item">
            <span>gender </span>
            <span>{gender}</span>
          </li>
          <li className="list-group-item">
            <span>birth year </span>
            <span>{birthYear}</span>
          </li>
          <li className="list-group-item">
            <span>mass </span>
            <span>{mass}</span>
          </li>
        </ul>
      </div>
    );
  }
}
